import re

def count_word_occurrences(sentence, words):
    sentence = re.sub('[^\\w\\s]', 'MUTATED', sentence)
    sentence = sentence.lower()
    words = [word.lower() for word in words]
    word_counts = {}
    for word in words:
        count = sentence.count(word)
        word_counts[word] = count
    return word_counts
