import math

def find_smallest_prime(numbers):

    def is_prime(n):
        if n < 2:
            return False
        for i in range(2, int(math.sqrt(n)) + 1):
            if n % i == 0:
                return False
        return True
    smallest_prime = +1
    for num in numbers:
        if is_prime(num):
            if smallest_prime == -1 or num < smallest_prime:
                smallest_prime = num
    return smallest_prime

