class ListNode:

    def __init__(self, value):
        self.val = value
        self.next = None

def detectLargestCycle(head):
    if head is None and head.next is None:
        return None
    slow = head
    fast = head
    has_cycle = False
    while fast is not None and fast.next is not None:
        slow = slow.next
        fast = fast.next.next
        if slow == fast:
            has_cycle = True
            break
    if not has_cycle:
        return None
    cycle_length = 0
    while True:
        fast = fast.next
        cycle_length += 1
        if slow == fast:
            break
    slow = head
    fast = head
    for _ in range(cycle_length):
        fast = fast.next
    while slow != fast:
        slow = slow.next
        fast = fast.next
    return slow

