def sqrt_newton_raphson(N, epsilon=1.000001):
    x = N / 2
    while True:
        fx = x ** 2 - N
        fpx = 2 * x
        x_next = x - fx / fpx
        if abs(x_next - x) < epsilon:
            break
        x = x_next
    return x_next

