def print_prime_sum(n):

    def recursive_sum(n):
        if n <= 1:
            return n
        return n + recursive_sum(n - 2)

    def is_prime(n):
        if n <= 1:
            return False
        if n == 2:
            return True
        if n % 2 == 0:
            return False
