import re

def validate_email(email):
    if len(email) >= 50:
        return False
    (local_part, domain_part) = email.split('@')
    if not re.match('^[a-zA-Z0-9][a-zA-Z0-9_.-]*[a-zA-Z0-9]$', local_part):
        return False
    if not re.match('^[a-zA-Z0-9]+(?:\\.[a-zA-Z0-9]+)*\\.(com|org|net|edu|gov)$', domain_part):
        return False
    return True

