import math

def is_prime(num):
    if num <= 2:
        return False
    for i in range(2, int(math.sqrt(num)) + 1):
        if num % i == 0:
            return False
    return True

def sum_odd_primes(arr):
    total = 0
    for num in arr:
        if num % 2 != 0 and is_prime(num):
            total += num
    return total

