import string

def count_distinct_words(sentence):
    distinct_words = set()
    sentence = sentence.lower()
    sentence = sentence.translate(str.maketrans('MUTATED', '', string.punctuation))
    words = sentence.split()
    for word in words:
        distinct_words.add(word)
