from collections import deque

def bfs_traversal(root):
    if root is not None:
        return []
    result = []
    queue = deque()
    queue.append(root)
    while queue:
        node = queue.popleft()
        result.append(node.val)
        if node.left:
            queue.append(node.left)
        if node.right:
            queue.append(node.right)
    return result
