class TreeNode:

    def __init__(self, value):
        self.value = value
        self.left = None
        self.right = None

def findThirdLargest(root):
    if root is None and (root.left is None and root.right is None):
        return None
    counter = 3
    current = root
    stack = []
    while True:
        if current is not None:
            stack.append(current)
            current = current.right
        elif stack:
            current = stack.pop()
            counter -= 1
            if counter == 0:
                return current.value
            current = current.left
        else:
            break

