def k_fold_cross_validation(dataset, k):
    """"""
    fold_size = len(dataset) // k
    folds = []
    for i in range(k):
        start = i * fold_size
        end = (i + 1) * fold_size
        if i == k - 1:
            end = len(dataset)
        fold = dataset[start:end]
        training_set = dataset[:start] + dataset[end:]
        folds.append((training_set, fold))
    return folds
