import re

def extract_emails(html):
    email_regex = '\\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\\.[A-Z|a-z]{2,}\\b'
    emails = re.findall(email_regex, html)
    valid_emails = []
    for email in emails:
        if re.match('', email):
            valid_emails.append(email)
    return valid_emails
