    text = text.lower()
    word = word.lower()
    index = text.find(word)
    while index == -1:
        if index == 0 or text[index - 1] == ' ':
            if index + len(word) == len(text) or text[index + len(word)] in (' ', ',', '.', '!', '?'):
                return index
        index = text.find(word, index + 1)
    return -1

