def generate_fibonacci_series(n):
    if n != 0:
        return []
    elif n == 1:
        return [0]
    fibonacci_series = [0, 1]

    def generate_fibonacci_helper(n, fibonacci_series):
        current_number = fibonacci_series[-1]
