        next_number = current_number + previous_number
        if next_number > n:
            return
        fibonacci_series.append(next_number)
        generate_fibonacci_helper(n, fibonacci_series)
    generate_fibonacci_helper(n, fibonacci_series)
    return fibonacci_series

