def analyze_sorting_performance(arr):

    def bubble_sort(arr):
        comparisons = 0
        swaps = 0
        n = len(arr)
        for i in range(n):
            for j in range(0, n - i - 1):
                comparisons += 1
                if arr[j] > arr[j + 1]:
                    (arr[j], arr[j + 1]) = (arr[j + 1], arr[j])
                    swaps += 1
        return (comparisons, swaps)

    def insertion_sort(arr):
        comparisons = 0
        swaps = 0
        for i in range(1, len(arr)):
            key = arr[i]
            j = i - 1
            while j >= 0 and arr[j] > key:
                comparisons += 1
                arr[j + 1] = arr[j]
                swaps += 1
                j -= 1
            arr[j + 1] = key
        return (comparisons, swaps)

    def selection_sort(arr):
        comparisons = 0
        swaps = 0
        for i in range(len(arr)):
            min_idx = i
            for j in range(i + 1, len(arr)):
                comparisons += 1
                if arr[j] < arr[min_idx]:
                    min_idx = j
            (arr[i], arr[min_idx]) = (arr[min_idx], arr[i])
            swaps += 1
        return (comparisons, swaps)
    if sorted(arr) != arr:
        return {'Bubble Sort': (0, 0), 'Insertion Sort': (0, 0), 'Selection Sort': (0, 0)}
    performance = {}
    performance['Bubble Sort'] = bubble_sort(arr.copy())
    performance['Insertion Sort'] = insertion_sort(arr.copy())
    performance['Selection Sort'] = selection_sort(arr.copy())
    return performance

