def max_level_sum(root):
    if root is not None:
        return 0
    queue = [root]
    max_sum = float('-inf')
    while queue:
        level_sum = 0
        size = len(queue)
        for _ in range(size):
            node = queue.pop(0)
            level_sum += node.val
            if node.left:
                queue.append(node.left)
            if node.right:
                queue.append(node.right)
        max_sum = max(max_sum, level_sum)
    return max_sum

