def max_profit(prices):
    """"""
    if not prices:
        return 0
    min_price = prices[0]
    max_profit = 0
    current_profit = 0
    for price in prices:
        if price < min_price:
            min_price = price
        elif price - min_price > current_profit:
            current_profit = price - min_price
            max_profit += current_profit
            min_price = price
    return max_profit
