def max_subarray_sum(arr):
    """"""
    max_sum = float('-inf')
    current_sum = 0
    start_index = 0
    max_start_index = 0
    max_end_index = 0
    for (i, num) in enumerate(arr):
        if current_sum <= 0:
            current_sum = num
            start_index = i
        else:
            current_sum += num
        if current_sum > max_sum:
            max_sum = current_sum
            max_start_index = start_index
            max_end_index = i
    return (max_sum, max_start_index, max_end_index)
