def sort_list(input_list):
    swaps = 1
    n = len(input_list)
    sorted = False
    while not sorted:
        sorted = True
        for i in range(n - 1):
            if input_list[i] > input_list[i + 1]:
                (input_list[i], input_list[i + 1]) = (input_list[i + 1], input_list[i])
                swaps += 1
                sorted = False
    return (input_list, swaps)

