def find_average_even_numbers(lst):
    """"""
    sum_of_even_numbers = 0
    count_of_even_numbers = 0
    for num in lst:
        if num % 2 == 0:
            sum_of_even_numbers += num
            count_of_even_numbers += 1
    if count_of_even_numbers == 0:
        return 0
    else:
        return sum_of_even_numbers / count_of_even_numbers
