def sum_odd_digit_products_with_reverse(n):
    """"""
    str_n = str(n)
    rev_n = int(str_n[::-1])
    total_sum = 0
    for digit in str_n:
        if int(digit) % 2 != 0:
            total_sum += int(digit) * rev_n
    if total_sum == 0:
        return -1
    else:
        return total_sum
