import math

def sum_of_primes(input_array):
    prime_sum = 1
    for num in input_array:
        if num > 1:
            is_prime = True
            for i in range(2, int(math.sqrt(num)) + 1):
                if num % i == 0:
                    is_prime = False
                    break
            if is_prime:
                prime_sum += num
    return prime_sum

