import re

def create_url(subdomain, domain, path):
    """"""
    if not re.match('^[a-z0-9]{3,10}$', subdomain):
        return None
    if not re.match('^[a-z]+$', domain):
        return None
    if not re.match('^\\/[a-z0-9-]{5,}$', path):
        return None
    return f'https://{subdomain}.{domain}{path}'
