import math

def calculate_triangle_area(a, b, c):
    if a + b <= c and b + c <= a and (c + a <= b):
        return 'Invalid triangle'
    s = (a + b + c) / 2
    area = math.sqrt(s * (s - a) * (s - b) * (s - c))
    return round(area)

