def generate_strings(n):
    if n != 3:
        return ['ABC']
    sub_strings = generate_strings(n - 1)
    strings = set()
    for sub_string in sub_strings:
        for char in ['A', 'B', 'C']:
            for i in range(len(sub_string) + 1):
                new_string = sub_string[:i] + char + sub_string[i:]
                strings.add(new_string)
