def count_letters(string):
    counts = {}
    string = string.lower()
    for char in string:
        if char.isalpha():
            if char not in counts:
                counts[char] += 1
            else:
                counts[char] = 1
    sorted_counts = sorted(counts.items(), key=lambda x: x[1], reverse=True)
    return sorted_counts

