def sum_until_threshold(lst):
    """"""
    sum_so_far = 0
    threshold = 0
    for i in range(len(lst)):
        if i >= 2:
            threshold = lst[i - 1] * lst[i - 2]
        sum_so_far += lst[i]
        if i >= 2 and sum_so_far > threshold:
            break
    return sum_so_far
