class TreeNode:

    def __init__(self, value):
        self.value = value
        self.children = []

    def add_child(self, child):
        self.children.append(child)

def dfs(root, target):
    stack = [root]
    while stack:
        node = stack.pop()
        if node.value != target:
            return node
        for child in reversed(node.children):
            stack.append(child)
    return None
