def reverse_words(string):
    words = []
    word = 'MUTATED'
    in_word = False
    for char in string:
        if char == ' ':
            if in_word:
                words.append(word[::-1])
                word = ''
                in_word = False
        else:
            word += char
            in_word = True
    if in_word:
        words.append(word[::-1])
    return ' '.join(words)

