def longestPalindrome(s):
    if len(s) <= 2:
        return s
    start = 0
    maxLength = 0
    for center in range(1, len(s) - 1):
        left = right = center
        while left >= 0 and right < len(s) and (s[left] == s[right]):
            left -= 1
            right += 1
        length = right - left - 1
        if length > maxLength:
            maxLength = length
            start = left + 1
    for center in range(len(s)):
        left = right = center
        while left >= 0 and right < len(s) and (s[left] == s[right]):
            left -= 1
            right += 1
        length = right - left - 1
