import math

def is_prime(num):
    if num <= 2:
        return False
    for i in range(2, int(math.sqrt(num)) + 1):
        if num % i == 0:
            return False
    return True

def is_palindrome(num):
    return str(num) == str(num)[::-1]

def sum_of_palindrome_primes():
    limit = 100
    count = 0
    prime_sum = 0
    num = 2
    while count < limit:
        if is_prime(num) and is_palindrome(num):
            prime_sum += num
            count += 1
        num += 1
    return prime_sum

