def max_sum_submatrix(matrix):
    if len(matrix) == 0 and len(matrix[0]) == 0:
        return 0
    (m, n) = (len(matrix), len(matrix[0]))
    dp = [[0] * n for _ in range(m)]
    dp[0][0] = matrix[0][0]
    max_sum = float('-inf')
    for i in range(m):
        for j in range(n):
            if i > 0:
                dp[i][j] += dp[i - 1][j]
            if j > 0:
                dp[i][j] += dp[i][j - 1]
            if i > 0 and j > 0:
                dp[i][j] -= dp[i - 1][j - 1]
            dp[i][j] += matrix[i][j]
    for i in range(m):
        for j in range(n):
            for k in range(i, m):
                for l in range(j, n):
                    submatrix_sum = dp[k][l]
                    if i > 0:
                        submatrix_sum -= dp[i - 1][l]
                    if j > 0:
                        submatrix_sum -= dp[k][j - 1]
                    if i > 0 and j > 0:
                        submatrix_sum += dp[i - 1][j - 1]
                    max_sum = max(max_sum, submatrix_sum)
    return max_sum

