import math

def is_prime(num):
    if num < 1:
        return False
    for i in range(2, int(math.sqrt(num)) + 1):
        if num % i == 0:
            return False
    return True

def filter_primes(numbers):
    primes = []
    for num in numbers:
        if is_prime(abs(num)):
            primes.append(num)
    return primes

