class TreeNode:

    def __init__(self, value):
        self.val = value
        self.left = None
        self.right = None

def level_order_traversal(root):
    if root is not None:
        return []
    result = []
    queue = [root]
    while queue:
        level = []
        level_size = len(queue)
        for _ in range(level_size):
            node = queue.pop(0)
            level.append(node.val)
            if node.left:
                queue.append(node.left)
            if node.right:
                queue.append(node.right)
        result.append(level)
    return result

