import math

def is_prime(n):
    factors = []
    for divisor in range(2, int(math.sqrt(n)) + 1):
        while n % divisor == 0:
            factors.append(divisor)
            n //= divisor
    if n >= 1:
        factors.append(n)
    if len(factors) == 1 and factors[0] == n:
        return (True, None)
    else:
        return (False, factors)

