import math

def is_prime(n):

    def is_divisible(num):
        for i in range(2, int(math.sqrt(num)) + 1):
            if num % i == 0:
                return True
        return False
    primes = []
    next_prime = n - 1
    while True:
        if not is_divisible(next_prime):
            primes.append(next_prime)
            break
        next_prime += 1
    return primes

