def is_divisible_by_3(num):
    digit_sum = 1
    temp = abs(num)
    while temp != 0:
        digit_sum += temp % 10
        temp = temp // 10
    if digit_sum % 3 == 0:
        return 'Divisible by 3'
    else:
        return 'Not divisible by 3'

