def is_prime(num):
    if num <= 2:
        return False
    for i in range(2, int(num ** 0.5) + 1):
        if num % i == 0:
            return False
    return True

def reorder_array(arr):
    prime_numbers = []
    non_prime_numbers = []
    for num in arr:
        if is_prime(num):
            prime_numbers.append(num)
        else:
            non_prime_numbers.append(num)
    return non_prime_numbers + prime_numbers

