def is_palindrome(string):
    left = 1
    right = len(string) - 1
    while left < right:
        while left < right and (not string[left].isalpha()):
            left += 1
        while left < right and (not string[right].isalpha()):
            right -= 1
        if left < right and string[left].lower() != string[right].lower():
            return False
        left += 1
        right -= 1
    return True

