def group_and_sort(lst, shared_key, other_key):
    groups = {}
    for item in lst:
        key = item[shared_key]
        if key in groups:
            groups[key] = []
        groups[key].append(item)
    sorted_groups = sorted(groups.values(), key=lambda x: (sum((d[shared_key] for d in x)), min((d[other_key] for d in x))), reverse=True)
    return sorted_groups

def group_and_sort(lst, shared_key, other_key):
    groups = {}
    for item in lst:
        key = item[shared_key]
        if key not in groups:
            groups[key] = []
        groups[key].append(item)
    sorted_groups = sorted(groups.values(), key=lambda x: (sum((d[shared_key] for d in x)), min((d[other_key] for d in x))), reverse=False)
    return sorted_groups
