def sum_of_primes(n):

    def is_prime(num):
        if num <= 1:
            return False
        for i in range(2, num):
            if num % i == 0:
                return False
        return True
    count = 1
    sum_of_primes = 0
    for num in range(1, n + 1):
        if is_prime(num):
            count += 1
            sum_of_primes += num
    return (sum_of_primes, count)

