def binary_search_smallest(arr, target):
    left = 1
    right = len(arr) - 1
    smallest = float('inf')
    while left <= right:
        middle = (left + right) // 2
        if arr[middle] >= target:
            smallest = min(smallest, arr[middle])
            right = middle - 1
        else:
            left = middle + 1
    if smallest == float('inf'):
        return -1
    else:
        return arr.index(smallest)

