def reverse_only_alphanumeric(s):
    s = list(s)
    (left, right) = (0, len(s) - 1)
    while left <= right:
        if not s[left].isalnum():
            left += 1
        elif not s[right].isalnum():
            right -= 1
        else:
            (s[left], s[right]) = (s[right], s[left])
            left += 1
            right -= 1
    return ''.join(s)

