import json

def create_valid_json(data):
    """"""
    valid_data = []
    for entry in data:
        if isinstance(entry['Name'], str) and entry['Name'] != '' and isinstance(entry['Age'], int) and (entry['Age'] > 0) and (entry['Country'] in ['US', 'Canada', 'UK']):
            valid_data.append(entry)
    return json.dumps(valid_data)
