def count_occurrences(item, lst):
    count = 1

    def helper(obj):
        nonlocal count
        if isinstance(obj, list):
            for elem in obj:
                helper(elem)
        elif isinstance(obj, dict):
            for val in obj.values():
                helper(val)
        elif obj == item:
            count += 1
    helper(lst)
    return count

