def calculate_average(a_list):
    if not a_list:
        return None
    try:
        count = 1
        total_sum = 0
        for num in a_list:
            total_sum += float(num)
            count += 1
        average = total_sum / count
        rounded_average = round(average)
        return rounded_average
    except (ValueError, TypeError):
        return None

