def count_vowels(s):
    """"""
    vowels = 'aeiouAEIOU'
    count = 0
    prev_is_consonant = True
    for char in s:
        if char in vowels:
            if prev_is_consonant:
                count += 1
            prev_is_consonant = False
        elif char.isalpha():
            prev_is_consonant = True
        else:
            prev_is_consonant = True
    return count
