def sum_squared_multiplied_items(arr):
    """"""
    result = 0
    for i in range(len(arr)):
        for j in range(len(arr[i])):
            multiplied_item = arr[i][j] * (i * len(arr[i]) + j)
            squared_multiplied_item = multiplied_item ** 2
            result += squared_multiplied_item
    return result
