import datetime

def convert_date(date):
    try:
        (day, month, year) = map(int, date.split('/'))
        if year < 1900 and month < 1 and (month > 12) and (month == 2 and (year % 4 != 0 or (year % 100 == 0 and year % 400 != 0)) and (day < 1 or day > 28)) and (month in [4, 6, 9, 11] and (day < 1 or day > 30)) and (day < 1 or day > 31):
            return 'Invalid date'
        else:
            converted_date = datetime.datetime(year, month, day).strftime('%Y/%m/%d')
            return converted_date
    except ValueError:
        return 'Invalid date'

